<script>
    window.addEventListener("load", function () {
        var timer = 0;
        var innerWidth = 0;
        var innerHeight = 0;
        var contentHeight = document.getElementById('main-content').offsetHeight;

        function initialize() {
            innerWidth = window.innerWidth
                || document.documentElement.clientWidth || document.body.clientWidth;
            innerHeight = window.innerHeight || document.documentElement.clientHeight || document.body.clientHeight;
        }

        function drawCanvas() {
            var canvas = document.getElementById('canvas');
            var ctx = canvas.getContext("2d");
            console.log(contentHeight);
            if (innerWidth > 1000) {
                if (innerHeight < contentHeight) {
                    innerHeight = contentHeight;
                }
                if (contentHeight < 750) {
                    innerHeight = 750;
                }
                document.getElementById('main-content').style.height = innerHeight + 'px';
            } else {
                if (innerHeight < document.getElementById('main-content').offsetHeight) {
                    innerHeight = document.getElementById('main-content').offsetHeight;
                }
                document.getElementById('main-content').style.height = contentHeight + 'px';
            }
            console.log(innerHeight);
            canvas.width = innerWidth;
            canvas.height = innerHeight;
            document.getElementById('canvas-box').style.height = innerHeight + 'px';
            document.getElementById('first_screen').style.height = innerHeight + 'px';

            ctx.lineWidth = 0;
            ctx.strokeStyle = '#00804c';
            ctx.beginPath();
            ctx.moveTo(0, 0.55 * innerHeight);
            ctx.lineTo(0.3 * innerWidth, innerHeight);
            ctx.lineTo(0, innerHeight);
            ctx.lineTo(0, 0, 55 * innerHeight);
            ctx.stroke();
            ctx.fillStyle = "#00804c";
            ctx.fill();
            ctx.closePath();

            ctx.beginPath();
            ctx.moveTo(0, 0);
            ctx.lineTo(0, 0.55 * innerHeight);
            ctx.lineTo(0.3 * innerWidth, innerHeight);
            ctx.lineTo(0.4 * innerWidth, innerHeight);
            ctx.lineTo(0.6 * innerWidth, 0);
            ctx.lineTo(0, 0);
            ctx.stroke();
            ctx.fillStyle = "#00a667";
            ctx.fill();
            ctx.closePath();

            ctx.beginPath();
            ctx.moveTo(0.6 * innerWidth, 0);
            ctx.lineTo(0.75 * innerWidth, 0);
            ctx.lineTo(innerWidth, 0.3 * innerHeight);
            ctx.lineTo(innerWidth, innerHeight);
            ctx.lineTo(0.4 * innerWidth, innerHeight);
            ctx.lineTo(0.6 * innerWidth, 0);
            ctx.stroke();
            ctx.fillStyle = "#00975a";
            ctx.fill();
            ctx.closePath();

            ctx.beginPath();
            ctx.moveTo(0.75 * innerWidth, 0);
            ctx.lineTo(innerWidth, 0);
            ctx.lineTo(innerWidth, 0.3 * innerHeight);
            ctx.lineTo(0.75 * innerWidth, 0);
            ctx.stroke();
            ctx.fillStyle = "#00804c";
            ctx.fill();
            ctx.closePath();
        }

        function initCanvas() {
            clearTimeout(timer);
            timer = setTimeout(function () {
                if (window.innerWidth != innerWidth) {
                    document.getElementById('main-content').style.height = '';
                    document.getElementById('canvas-box').style.height = '';
                    document.getElementById('first_screen').style.height = '';
                    var canvas = document.getElementById('canvas');
                    canvas.width = 0;
                    canvas.height = 0;
                    timer = 0;
                    innerWidth = 0;
                    innerHeight = 0;
                    contentHeight = document.getElementById('main-content').offsetHeight;
                    initialize();
                    drawCanvas();
                }
            }, 200);
        }

        window.addEventListener("resize", initCanvas);
        initCanvas();
    });
</script>
